import { Component } from '@angular/core';
import { WeatherData } from 'src/models/weather.model';
import { WeatherService } from 'src/services/weather.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private weatherService: WeatherService){

  }

  cityName: string = 'alaska';
  weatherData ? : WeatherData;

  ngOnInit(): void {
    this.getCurrentWeather(this.cityName);
    console.log(this.weatherData);
  }

  onSubmit(){
    this.getCurrentWeather(this.cityName);
    this.cityName = "";
  }


 private getCurrentWeather(cityName : string){
  this.weatherService.getCurrentWeather(cityName)
    .subscribe({
      next : (response) => {
        this.weatherData = response;
        console.log(response);
      }
    });
 }

}
<div class="search">
  <form #form = "ngForm" (submit) = "onSubmit()">
    <input type="text" placeholder="Search city" name = 'city' [(ngModel)] = "cityName">
  </form>
 </div>

 <div class = "container" *ngIf = "weatherData"> 

  <div class = "upper-data">
    <img src = "../assets/coldW(1).jpg" alt = "hot" *ngIf = "weatherData.main.temp > 15">
    <img src = "../assets/sunny.jpg" alt = "sunnt" *ngIf = "weatherData.main.temp < 15">
    <div class="weather-data">
      <div class="location">{{weatherData.name}}</div>
    <div class="temparature">{{weatherData.main.temp | number : '1.0-0'}} °C</div>
    </div>
  </div>

  <div class="lower-data">
    <div class="more-info-label">
      More Information
    </div>
    <div class="more-info-container">

      <div class="info-block">
        <div class="info-block-label">
          <img src = "../assets/mintemp.png">
          <span>min</span>
        </div>
        <div class="info-block-value">
          {{weatherData.main.temp_min}}°C
        </div>
      </div>

      <div class="info-block">
        <div class="info-block-label">
          <img src = "../assets/max.png">
          <span>max</span>
        </div>
        <div class="info-block-value">
          {{weatherData.main.temp_max}}°C
        </div>
      </div>

      <div class="info-block">
        <div class="info-block-label">
          <img src = "../assets/humidity.png">
          <span>humidity</span>
        </div>
        <div class="info-block-value">
          {{weatherData.main.humidity}}%
        </div>
      </div>

      <div class="info-block">
        <div class="info-block-label">
          <img src = "../assets/wind.png">
          <span>wind</span>
        </div>
        <div class="info-block-value">
          {{weatherData.wind.speed}}kmph
        </div>
      </div>

    </div>
  </div>

</div>

<router-outlet></router-outlet>
